import React, { Component } from "react";
import { getCategories } from "../services/fakeCategoriesService";

class Categorie extends Component {
  state = {
    categories: getCategories(),
    nbCategorieParPage: 2,
    pageEncours: 1
  };
  delete = categorie => {
    const filtredCategorie = this.state.categories.filter(
      c => c.id != categorie._id
    );
    this.setState({ categories: filtredCategorie });
  };
  render() {
    console.log(this.state.categories);

    return (
      <React.Fragment>
        <table className="table">
          <thead>
            <tr>
              <th scope="col">#</th>
              <th scope="col">Nom</th>
              <th scope="col">Action</th>
            </tr>
          </thead>
          <tbody>
            {this.state.categories.map(categorie => {
              return (
                <tr key={categorie._id}>
                  <th scope="row">{categorie._id}</th>
                  <td>{categorie.nom}</td>
                  <td>
                    <button
                      className="btn btn-danger btn-sm"
                      onClick={() => this.delete(categorie)}
                    >
                      supprimer
                    </button>
                  </td>
                  >
                </tr>
              );
            })}
          </tbody>
        </table>
        <Pagination
          totalArticles={this.state.categories.length}
          nbArticleParPage={this.state.nbCategorieParPage}
          pageEncours={this.state.pageEncours}
          gestionClick={this.gestionClick} // Le composant qui possède le state doit être celui qui le modifie
        />
      </React.Fragment>
    );
  }
}

export default Categorie;
